public class Ejercicio_19 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=1;
		int numero = 4;
		String tabla="";
		
		while (i<=10) {
			tabla = numero +"X"+i +"="+ (i*numero); 
			System.out.println(tabla);
			i++;
		}
		
	}

}
